// Meeting.jsx
import React, { useState } from 'react';
import CalendarComponent from './calendar';
import emailjs from 'emailjs-com';
import './meeting.css';

const Meeting = () => {
  const [events, setEvents] = useState([]);
    
  const handleSelectSlot = (slotInfo) => {
    const name = prompt('Enter your name to book this meeting slot:');
    if (name) {
      const newEvent = {
        title: `Meeting with ${name}`,
        start: slotInfo.start,
        end: slotInfo.end,
      };

      setEvents(prev => [...prev, newEvent]);

      // Email notification
      emailjs.send(
        'your_service_id',
        'your_template_id',
        {
          to_name: name,
          meeting_time: slotInfo.start.toString(),
        },
        'your_user_id'
      ).then(() => {
        alert('Meeting booked & email sent!');
      }).catch(err => console.error(err));
    }
  };

  return (
    
    <div className="meeting-page">
      <h2>Book a Meeting</h2>
      <p>Select a time slot below to schedule your session.</p>
      <CalendarComponent events={events} onSelectSlot={handleSelectSlot} />
    </div>
  );
};

export default Meeting;
