// Meeting.jsx
import React, { useState } from 'react';
import CalendarComponent from './calendar'; // make sure the path is correct
import emailjs from 'emailjs-com';
import './meeting.css'; // ensure the CSS file is imported correctly

const Meeting = () => {
  const [events, setEvents] = useState([]);

  const handleSelectSlot = (slotInfo) => {
    const name = prompt('Enter your name to book this meeting slot:');
    if (name) {
      const newEvent = {
        title: `Meeting with ${name}`,
        start: slotInfo.start,
        end: slotInfo.end,
      };
  
      setEvents(prev => [...prev, newEvent]);
    }
  };
  

  // **Return statement goes here!**
  return (
    <div className="meeting-page">
      <h1>HELLO THIS IS THE MEETING PAGE</h1> {/* This should show up to confirm the page is working */}
      <CalendarComponent events={events} onSelectSlot={handleSelectSlot} />
    </div>
  );
};

export default Meeting;
