// Meeting.jsx
import React, { useState } from 'react';
import CalendarComponent from './calendar'; // Ensure correct path
import emailjs from 'emailjs-com';
import './meeting.css'; // Ensure CSS is loaded

const Meeting = () => {
  const [events, setEvents] = useState([]);

  const handleSelectSlot = (slotInfo) => {
    const name = prompt('Enter your name to book this meeting slot:');
    if (name) {
      const newEvent = {
        title: `Meeting with ${name}`,
        start: slotInfo.start,
        end: slotInfo.end,
      };

      console.log('New Event:', newEvent); // Debugging

      setEvents(prev => [...prev, newEvent]);

      // Optional: Send email using emailjs
      emailjs.send(
        'your_service_id',
        'your_template_id',
        {
          to_name: name,
          meeting_time: slotInfo.start.toString(),
        },
        'your_user_id'
      ).then(() => {
        alert('Meeting booked & email sent!');
      }).catch(err => console.error('Email Error:', err));
    }
  };

  // Debugging: Check events when rendering
  console.log('Events in Meeting Component:', events);

  return (
    <div className="meeting-page">
      <h1>HELLO THIS IS THE MEETING PAGE</h1>
      <CalendarComponent events={events} onSelectSlot={handleSelectSlot} />
    </div>
  );
};

export default Meeting;
