import React, { useState, useEffect } from 'react';
import './meeting.css'; // Import the CSS file
import { Button } from '@/components/ui/button'; // If you are using shadcn/ui

const Meeting = () => {
  const [scheduledEvents, setScheduledEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showCalendly, setShowCalendly] = useState(false);
  const calendlyRef = useRef(null);

  // Function to fetch scheduled events from your backend
  const fetchScheduledEvents = async () => {
    setLoading(true);
    setError(null);
    try {
      // Replace '/api/scheduled_events' with your actual backend endpoint
      const response = await fetch('/api/scheduled_events');
      if (!response.ok) {
        throw new Error(`Failed to fetch events: ${response.status}`);
      }
      const data = await response.json();
      setScheduledEvents(data.collection || []); // Assuming your backend returns { collection: [] }
    } catch (err) {
      setError(err.message || 'An error occurred while fetching events.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Fetch events when the component mounts
    fetchScheduledEvents();
     // Load Calendly script
    const script = document.createElement('script');
    script.src = 'https://assets.calendly.com/assets/external/widget.js';
    script.async = true;
    document.body.appendChild(script);

    return () => {
      // Clean up the script tag when the component unmounts
      if (document.body.contains(script)) {
        document.body.removeChild(script);
      }
    };
  }, []);

    const handleScheduleMeeting = () => {
      setShowCalendly(true);
    };

    useEffect(() => {
        if (showCalendly) {
            // Initialize Calendly popup widget
             window.Calendly.initPopupWidget({
                url: 'https://calendly.com/your_calendly_name', // replace with your calendly url
            });
        }
    }, [showCalendly]);

  return (
    <div className="meeting-container">
      <h1 className="meeting-header">Schedule a Meeting</h1>

      <div className="meeting-schedule-section">
        <Button onClick={handleScheduleMeeting} className="meeting-button">
            Schedule a Meeting with Me
        </Button>
        {showCalendly && (
            <div
              ref={calendlyRef}
              className="calendly-inline-widget"
              data-url="https://calendly.com/your_calendly_name" // Replace with your Calendly link
              style={{ minWidth: '320px', height: '700px' }}
            />
        )}
      </div>

      <div className="meeting-events-section">
        <h2>Your Upcoming Meetings</h2>
        {loading && <p className="meeting-loading">Loading upcoming meetings...</p>}
        {error && <p className="meeting-error">Error: {error}</p>}
        {!loading && !error && (
          <ul className="meeting-list">
            {scheduledEvents.length > 0 ? (
              scheduledEvents.map((event) => (
                <li key={event.uri} className="meeting-list-item">
                  <div className="meeting-details">
                    <p>
                      <strong>Event:</strong> {event.name}
                    </p>
                    <p>
                      <strong>Date:</strong> {new Date(event.start_time).toLocaleString()}
                    </p>
                    <p>
                      <strong>Attendees:</strong> {event.invitees_counter}
                    </p>
                    {/* Add more event details as needed */}
                  </div>
                </li>
              ))
            ) : (
              <li className="meeting-list-item">No upcoming meetings.</li>
            )}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Meeting;
