import React, { Component } from 'react';

class MeetingPage extends Component {
  constructor(props) { // Added constructor
    super(props);  // Added super(props)
    this.state = {
      isCalendlyLoaded: false, // Track script load state
    };
  }

  componentDidMount() {
    const head = document.querySelector('head');
    const script = document.createElement('script');
    script.src = 'https://assets.calendly.com/assets/external/widget.js';
    script.async = true;
    script.onload = () => {
      this.setState({ isCalendlyLoaded: true }, () => {
        if (window.Calendly) {
          window.Calendly.initInlineWidgets(); // This is the key!
        }
      });
    };
    script.onerror = () => {
      console.error('Failed to load Calendly script');
    };
    head.appendChild(script);
  }
  

  render() {
    const { isCalendlyLoaded } = this.state;

    return (
      <div>
        <h1>Schedule a Meeting</h1> {/* Added a heading for context */}
        {isCalendlyLoaded ? (
          <div
            className="calendly-inline-widget"
            data-url="https://calendly.com/ttaayy82823"
            style={{ minWidth: '320px', height: '580px' }}
          />
        ) : (
          <div>Loading Calendly...</div> // Show loading message
        )}
      </div>
    );
  }
}

export default MeetingPage;
