import React from "react";
import "./feedpage.css";
import FBI_Internship from './FeedPage_Images/FBI_Internship.jpeg';
import Chevron_TMCF from './FeedPage_Images/Chevron_TMCF.jpeg'
import Harvard_BusinessSchool from './FeedPage_Images/Harvard_BusinessSchool.jpeg'
const FeedPage = () => {
  const newsArticles = [
    {
      id: 1,
      title: "FBI HONORS PROGRAM INTERNSHIP 2026",
      content: "Description:10-week paid internship for students pursuing associate, bachelor's, and advanced degrees who are ready work in federal law enforcement. ",
      image: FBI_Internship,
      author: "Alice Johnson",
      date: "Posted: March 18, 2025",
      link: "https://ow.ly/2QzB50VtytO"
    },
    {
      id: 2,
      title: "Chevron Energy Innovation Summit",
      content: "Description: A 4-day immersive experience hosted by TMCF and Chevron for HBCU/PBI freshmen and sophomores interested in the energy sector. Participants will engage in business case competitions, network with Chevron executives, and gain access to internship opportunities, scholarships, and exclusive industry insights. Application deadline: May 12, 2025.",
      image: Chevron_TMCF,
      author: "Bob Williams",
      date: "March 17, 2025",
      link:"https://careers-tmcf.icims.com/jobs/1574/2025-chevron-energy-summit"
    },
    {
      id: 3,
      title: "New Discovery in Space",
      content: "Scientists have discovered a new exoplanet that could support life...",
      image: hA,
      author: "Charlie Smith",
      date: "March 16, 2025"
    },
    {
      id: 4,
      title: "Global Markets See Significant Change",
      content: "Stock markets around the world experienced unexpected fluctuations...",
      image: "https://via.placeholder.com/300",
      author: "Timothy Clark",
      date: "March 15, 2025"
    },
    {
      id: 5,
      title: "Breaking: Major Event Unfolds",
      content: "A major event is happening right now with significant global impact...",
      image: "https://via.placeholder.com/300",
      author: "Alice Johnson",
      date: "March 18, 2025"
    },
    {
      id: 6,
      title: "Breaking: Major Event Unfolds",
      content: "A major event is happening right now with significant global impact...",
      image: "https://via.placeholder.com/300",
      author: "Alice Johnson",
      date: "March 18, 2025"
    },
    {
      id: 7,
      title: "Global Markets See Significant Change",
      content: "Stock markets around the world experienced unexpected fluctuations...",
      image: "https://via.placeholder.com/300",
      author: "Timothy Clark",
      date: "March 15, 2025"
    },
    {
      id: 8,
      title: "Global Markets See Significant Change",
      content: "Stock markets around the world experienced unexpected fluctuations...",
      image: "https://via.placeholder.com/300",
      author: "Timothy Clark",
      date: "March 15, 2025"
    },
    {
      id: 9,
      title: "Global Markets See Significant Change",
      content: "Stock markets around the world experienced unexpected fluctuations...",
      image: "https://via.placeholder.com/300",
      author: "Timothy Clark",
      date: "March 15, 2025"
    },
  ];

  return (
    <div className="feed-container">
      <h2 className="feed-title">Opportunities and News!</h2>
      <div className="feed-list">
        {newsArticles.map((article) =>
          article.link ? (
            <a
              key={article.id}
              href={article.link}
              target="_blank"
              rel="noopener noreferrer"
              className="feed-link-wrapper"
            >
              <div className="feed-item">
                <img src={article.image} alt={article.title} className="feed-image" />
                <h3 className="feed-headline">{article.title}</h3>
                <p className="feed-content">{article.content}</p>
                <p className="feed-meta">By {article.author} • {article.date}</p>
              </div>
            </a>
          ) : (
            <div key={article.id} className="feed-item">
              <img src={article.image} alt={article.title} className="feed-image" />
              <h3 className="feed-headline">{article.title}</h3>
              <p className="feed-content">{article.content}</p>
              <p className="feed-meta">By {article.author} • {article.date}</p>
            </div>
          )
        )}
      </div>
    </div>
  );
};


export default FeedPage;
